import { useEffect, useReducer } from "react";

const requestStatus = {
  initial: "initial",
  loading: "loading",
  fulfilled: "fulfilled",
  error: "error",
};


function reducer(state, action) {
  switch (action.type) {

    case requestStatus.loading:
      return { ...state, status: requestStatus.loading };
    case requestStatus.fulfilled:
      return {
        ...state,
        status: requestStatus.fulfilled,
        data: action.payload.data,
      };
    default:
      return {
        ...state,
        status: requestStatus.error,
        error: action.payload.error,
      };
  }
}

function App() {
  const [{ status, error, data }, dispatch] = useReducer(
    reducer,


  );
console.log(status)
  const func = function (){
    fetch("https://restcountries.com/v3.1/name/russia?fullText=true")
      .then(async (res) => {

        await dispatch({

            type: requestStatus.loading,
          });

        const json = await res.json();

        if (!res.ok) {
          throw new Error(json.message);
        }

        return json;
      })
      .then((prop) => {
        dispatch({ type: requestStatus.fulfilled, payload: { data: prop } });
      })
      .catch((err) => {
        dispatch({
          type: requestStatus.error,
          payload: { error: { message: err.message } },
        });
      });
    }

  return (

    <div className="App">
      <button onClick={func}></button>
      {status === requestStatus.loading ? (
        <h1>...loading</h1>
      ) : status === requestStatus.fulfilled && data?.length ? (
        data.map((country) => {
          return (
            <div key={country.name} className="filmContainer">
              <div>{`Country Name: ${country.name.common}`}</div>
            </div>
          );
        })
      ) : status === requestStatus.error && error?.message ? (
        <div>{error.message}</div>
      ) : null}
    </div>
  );
}

export default App;
