import { useReducer, useState } from "react";

const initialState = 0;




function reducer(statee, action) {
  switch (action.type) {
    case "increment":
      if(statee.countt<5){
        console.log(statee)
      return { count: statee.countt + 1 };
      }else{
        return {count:0}
      }
    case "decrement":
      return { count: statee.count - 1 };
    case "reset":
      return { count: 0 };
    case "add12":
      return { count: statee.count + action.payload.quantity };
    default:
      throw new Error();
  }
}

function Counter() {
  const [statee, dispatch] = useReducer(reducer, initialState);

  return (
    <>
      Coun: {statee.count}
      <button onClick={() => dispatch({ type: "decrement" })}>-</button>
      <button onClick={() => dispatch({ type: "increment" })}>+</button>
      <button onClick={() => dispatch({ type: "reset" })}>reset</button>
      <button
        onClick={() => dispatch({ type: "add12", payload: { quantity: 12 } })}
      >
        add 12
      </button>
      <input></input>
    </>
  );
}
export default Counter
