import { useReducer, useState } from "react";

const initialState = { count: 0 };




function reducer(state, action) {
  switch (action.type) {
    case "increment":
      return { count: action.payload.quantity + 1 };
    case "decrement":
      return { count: state.count - action.payload.quantity };
    case "reset":
      return { count: 0 };
    case "add12":
      return { count: action.payload.quantity };
    default:
      throw new Error();
  }
}

function Counter() {
  const [state, dispatch] = useReducer(reducer, initialState);
  const [val,setval] = useState(0)

  const v = function(e){
    setval(e.target.value)
  }

  return (
    <>
      Count: {state.count}
      <button onClick={() => dispatch({ type: "decrement",payload: { quantity: val }})}>-</button>
      <button onClick={() => dispatch({ type: "increment" ,payload: { quantity: val }})}>+</button>
      <button onClick={() => dispatch({ type: "reset" })}>reset</button>
      <button
        onClick={() => dispatch({ type: "add12", payload: { quantity: val } })}
      >
        add 12
      </button>
      <input onChange={v}></input>
    </>
  );
}
export default Counter
